INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55947, 1062, 'Hydroxyzine', 'Asthma', 'It has been suggested that the anticholinergic effect of antihistamines may reduce the volume and cause thickening of bronchial secretions, resulting in obstruction of respiratory tract.  Some manufacturers and clinicians recommend that therapy with antihistamines be administered cautiously in patients with asthma or chronic obstructive pulmonary disease.', '2', '', 'DDInter', 0);
