INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55949, 1062, 'Hydroxyzine', 'Cardiovascular Diseases', 'Antihistamines may infrequently cause cardiovascular adverse effects related to their anticholinergic and local anesthetic (quinidine-like) activities.  Tachycardia, palpitation, ECG changes, arrhythmias, hypotension, and hypertension have been reported.  Although these effects are uncommon and usually limited to overdosage situations, the manufacturers and some clinicians recommend that therapy with antihistamines be administered cautiously in patients with cardiovascular disease, hypertension, and/or hyperthyroidism.', '2', '', 'DDInter', 0);
