INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55971, 20724, 'Ibrutinib', 'Pancytopenia', 'Cytopenias including neutropenia, thrombocytopenia, and anemia have been reported with use of ibrutinib.  Care should be taken with patients at risk, and it is recommended to monitor for complete blood counts monthly or as clinically appropriate.', '2', '', 'DDInter', 0);
