INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55975, 20724, 'Ibrutinib', 'Hypertension', 'The use of ibrutinib may cause hypertension.  It is recommended to monitor for new onset hypertension or hypertension that is not adequately controlled after starting therapy and to adjust hypertensive medications and/or initiate antihypertensive treatment per clinical practices.  Close monitoring of blood pressure is recommended.', '2', '', 'DDInter', 0);
