INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55981, 20724, 'Ibrutinib', 'Tumor Lysis Syndrome', 'Tumor lysis syndrome has been reported with ibrutinib therapy.  It is recommended to assess the baseline risk (e.g., high tumor burden) and take appropriate precautions.  Monitor patients closely and treat as appropriate.', '2', '', 'DDInter', 0);
