INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57257, 13647, 'Imiquimod (topical)', 'Eczema', 'Imiquimod may exacerbate inflammatory conditions of the skin.  Local reactions are commonly associated with the use of imiquimod and may extend beyond the application site onto the surrounding skin.  These reactions include erythema, edema, vesicles, erosion/ulceration, weeping/exudate, flaking/scaling/dryness, and scabbing/crusting.  Therapy with topical imiquimod should be avoided or used with caution in patients with eczematous conditions.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57258, 13647, 'Imiquimod (topical)', 'Sunburn', 'The use of imiquimod may be associated with heightened sunburn susceptibility.  Patients with sunburn should be cautioned not to use imiquimod until fully recovered.  Exposure to sunlight, including sunlamps, should be avoided or minimized during treatment, and protective clothing (e.g., hat) is recommended when exposure is unavoidable.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57259, 4745, 'Imatinib', 'Cardiovascular Diseases', 'Cardiovascular events, including arterial vascular occlusive events, peripheral arterial occlusive events and ischemic cerebrovascular events have been reported in patients receiving tyrosine kinase inhibitors.  If acute signs or symptoms of cardiovascular events occur, patients should seek immediate medical attention.  The cardiovascular status and risk factors of patients should be evaluated prior to therapy and cardiovascular monitoring and management should take place during treatment.', '2', '', 'DDInter', 0);
