INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57291, 4745, 'Imatinib', 'Hepatic Insufficiency', 'Patients with severe hepatic impairment tend to have higher exposure to both imatinib and its metabolite than patients with normal hepatic function, and dose might need to be adjusted accordingly.  Additionally, there have been reports of hepatotoxicity both with short and long term use of imatinib.  Liver function should be monitored before initiation of treatment and regularly during therapy.  Dose reduction or interruption might be needed if laboratory abnormalities are found.', '2', '', 'DDInter', 0);
