INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57350, 6404, 'Imipramine', 'Urinary Retention', 'Due to their anticholinergic properties, tricyclic antidepressants should be administered with caution in patients with history of urinary retention.  Particularly doxepin hydrochloride capsules are contraindicated in patients with tendency to urinary retention.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57351, 11485, 'Imipramine', 'Urinary Retention', 'Due to their anticholinergic properties, tricyclic antidepressants should be administered with caution in patients with history of urinary retention.  Particularly doxepin hydrochloride capsules are contraindicated in patients with tendency to urinary retention.', '2', '', 'DDInter', 0);
