INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57356, 7245, 'Insulin detemir', 'Hypoglycemia', 'Hypoglycemia may commonly occur during treatment with insulin and/or oral hypoglycemic agents.  Care should be taken in patients who may be particularly susceptible to the development of hypoglycemic episodes during the use of these drugs, including those who are debilitated or malnourished, those with defective counterregulatory mechanisms (e.g., autonomic neuropathy and adrenal or pituitary insufficiency), and those receiving beta-adrenergic blocking agents.', '2', '', 'DDInter', 0);
