INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57393, 0, 'Insulin human (inhalation, rapid acting)', 'Asthma', 'The use of inhaled insulin is contraindicated in patients with chronic lung disease such as asthma and COPD.  Acute bronchospasm has been observed after an inhaled insulin dosage.  Prior to initiating therapy with an inhaled insulin product, it is recommended to evaluate all patients medical history, physical examination and spirometry (FEV1) to identify potential underlying lung disease.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57394, 0, 'Insulin human (inhalation, rapid acting)', 'Diabetic Ketoacidosis', 'Rapid acting inhaled insulin may predispose certain patients to develop diabetic ketoacidosis (DKA).  In clinical trials, subjects with type 1 diabetes receiving inhaled insulin were more likely to develop DKA than patients receiving comparator agents.  It is recommended to increase the frequency of glucose monitoring and to consider using an alternate route of administration, if appropriate, in patients at risk for DKA, such as patients with an acute illness or infections.', '2', '', 'DDInter', 0);
