INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57401, 0, 'Insulin human', 'Hypoglycemia', 'Hypoglycemia may commonly occur during treatment with insulin and/or oral hypoglycemic agents.  Care should be taken in patients who may be particularly susceptible to the development of hypoglycemic episodes during the use of these drugs, including those who are debilitated or malnourished, those with defective counterregulatory mechanisms (e.g., autonomic neuropathy and adrenal or pituitary insufficiency), and those receiving beta-adrenergic blocking agents.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57402, 0, 'Insulin human (isophane)', 'Liver Diseases', 'Insulin requirements may be diminished in patients with renal or hepatic impairment due to reduced insulin metabolism and, in the latter case, also to reduced capacity for gluconeogenesis.  Lower initial dosages may be appropriate, with careful monitoring of plasma glucose levels and dosing adjustments.', '2', '', 'DDInter', 0);
