INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57476, 0, 'Insulin human (zinc)', 'Hypokalemia', 'Hypokalemia may commonly occur during treatment with insulin.  Use with caution in patients who may be particularly at risk of developing hypokalemia (e.g., patients using potassium-lowering medications, patients taking medications sensitive to serum potassium concentrations).', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57477, 0, 'Insulin human (zinc)', 'Hypoglycemia', 'Hypoglycemia may commonly occur during treatment with insulin and/or oral hypoglycemic agents.  Care should be taken in patients who may be particularly susceptible to the development of hypoglycemic episodes during the use of these drugs, including those who are debilitated or malnourished, those with defective counterregulatory mechanisms (e.g., autonomic neuropathy and adrenal or pituitary insufficiency), and those receiving beta-adrenergic blocking agents.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57478, 4034, 'Interferon alfa-2b', 'Kidney Diseases', 'Proteinuria (rare), not associated with a decrease in serum protein, is the most common renal toxicity observed with interferon- alfa therapy.  Increased BUN and serum creatinine levels, hematuria, acute renal failure and nephrotic syndrome have also been reported.  Therapy with interferon alfa should be administered cautiously to patients with severe renal dysfunction.  Renal function should be evaluated in all patients before initiation of interferon- alfa therapy.  In addition, patients with any degree of renal impairment should be carefully monitored for laboratory abnormalities and decreased creatinine clearance.  A reduced dose of medication is recommended in patients with low CrCl (less than 50 mL/min - 30 mL/min).', '3', '', 'DDInter', 0);
