INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57546, 20894, 'Interferon alfacon-1', 'Kidney Diseases', 'Proteinuria (rare), not associated with a decrease in serum protein, is the most common renal toxicity observed with interferon- alfa therapy.  Increased BUN and serum creatinine levels, hematuria, acute renal failure and nephrotic syndrome have also been reported.  Therapy with interferon alfa should be administered cautiously to patients with severe renal dysfunction.  Renal function should be evaluated in all patients before initiation of interferon- alfa therapy.  In addition, patients with any degree of renal impairment should be carefully monitored for laboratory abnormalities and decreased creatinine clearance.  A reduced dose of medication is recommended in patients with low CrCl (less than 50 mL/min - 30 mL/min).', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57547, 20894, 'Interferon alfacon-1', 'Autoimmune Diseases', 'Antiviral interferons may exacerbate autoimmune disorders such as myositis, thyroiditis, systemic lupus erythematosus, rheumatoid arthritis, psoriasis, or autoimmune hepatitis.  Therapy should be avoided or administered with extreme caution in patients with autoimmune disorders.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57548, 20894, 'Interferon alfacon-1', 'Liver Diseases', 'Dose-related increases in liver transaminase levels occur frequently during interferon-alfa therapy.  Changes in LDH and alkaline phosphatase have been reported, primarily in patients with preexisting hepatic impairment.  Patients with autoimmune hepatitis who have received interferon-alfa have experienced severe, life-threatening exacerbation of their disease.  Therapy with interferon-alfa should be administered cautiously in patients with liver disease and should not be initiated in patients with autoimmune hepatitis.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57549, 20894, 'Interferon alfacon-1', 'Heart Diseases', 'Hypertension, arrhythmias, angina, myocardial infarction, cardiomyopathy, and congestive heart failure have been reported during interferon therapy.  Therapy with interferons should be administered with caution in patients with compromised cardiac function.', '3', '', 'DDInter', 0);
