INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57601, 14107, 'Anastrozole', 'Liver Diseases', 'Anastrozole is extensively metabolized by the liver.  Analysis of the pharmacokinetic disposition of anastrozole in patients with hepatic cirrhosis indicates that dosage adjustments of anastrozole are not necessary in patients with mild to moderate hepatic impairment.  Clinical monitoring for toxicity associated with anastrozole therapy is recommended.  Therapy with anastrozole should be administered cautiously in patients with severe hepatic impairment.', '2', '', 'DDInter', 0);
