INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57618, 12375, 'Iodixanol', 'Pheochromocytoma', 'Administration of radiopaque materials to patients known or suspected of having pheochromocytoma should be performed with extreme caution.  If, in the opinion of the physician, the possible benefits of such procedures outweigh the considered risk, the procedures should be performed with the absolute minimum amount of injected radiopaque medium.  The blood pressure should be assessed throughout the procedure and measures for treatment of a hypertensive crisis should be available.', '3', '', 'DDInter', 0);
