INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57737, 1408, 'Iron Dextran', 'Hemoglobinopathies', 'There is no excretory mechanism for iron.  Iron will correct only hemoglobin abnormalities due to iron deficiency and should not be used to treat conditions such as thalassemia, hemosiderosis, hemochromatosis, normocytic anemia (unless iron deficiency exists), or in patients receiving blood transfusions.  Clinical monitoring of erythropoietic function and ferritin levels is recommended.', '3', '', 'DDInter', 0);
