INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57784, 1408, 'Iron Dextran', 'Liver Diseases', 'Iron is transported in plasma complexed to transferrin, a beta1-glycoprotein synthesized by the liver.  Iron is cleared from the plasma by the reticuloendothelial system (including liver) and excess stored as ferritin within the reticuloendothelial system and hepatocytes.  Therapy with iron dextran should be administered with extreme caution in patients with severe hepatic dysfunction.', '3', '', 'DDInter', 0);
