INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58019, 1962, 'Irinotecan', 'Bone Marrow Failure Disorders', 'Irinotecan induces myelosuppression, causing leukopenia, thrombocytopenia and anemia.  Deaths due to sepsis following severe neutropenia have been reported in patients treated with irinotecan.  Hold irinotecan if neutropenic fever occurs or if the absolute neutrophil count drops <1000/mm3.  After recovery to an absolute neutrophil count =1000/mm3, subsequent doses of irinotecan should be reduced.  Therapy with irinotecan should be administered cautiously in patients who have received previous irradiation therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended.', '3', '', 'DDInter', 0);
