INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58028, 1962, 'Irinotecan', 'Liver Diseases', 'Irinotecan clearance is diminished in patients with hepatic impairment.  The magnitude of these effects is proportional to the degree of liver impairment as measured by elevations in total bilirubin and transaminase concentrations.  However, the tolerability of irinotecan in patients with hepatic dysfunction (bilirubin greater than 2 mg/dl) has not been assessed sufficiently, and no recommendations for dosing are made by the manufacturers.  Therapy with irinotecan should be administered cautiously to patients with or predisposed to compromised hepatic function.  Clinical monitoring of hepatic function is recommended.', '2', '', 'DDInter', 0);
