INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58046, 1962, 'Irinotecan', 'Lung Diseases, Interstitial', 'Irinotecan can cause severe and fatal interstitial pulmonary disease (IPD).  Risk factors include preexisting-existing lung disease.  It is recommended to withhold treatment with irinotecan in patients with new or progressive dyspnea, cough, and fever, pending diagnostic evaluation and to discontinue treatment in patients with a confirmed diagnosis of IPD.  Exercise caution when using this agent in patients presenting symptoms of dyspnea and patients with pulmonary impairment.', '2', '', 'DDInter', 0);
