INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58229, 884, 'Irbesartan', 'Liver Diseases', 'Irbesartan is metabolized by the liver, and both parent drug and metabolites are eliminated by the kidney (20%) as well as by biliary excretion (80%).  Dosage adjustments are not necessary in patients with renal impairment unless they are also volume-depleted, in which case therapy should be initiated under medical supervision.  Likewise, patients with hepatic impairment or biliary obstruction generally do not require a dosage adjustment.  However, reduced dosages may be appropriate in patients with both renal and liver or biliary disease.', '2', '', 'DDInter', 0);
