INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58282, 6615, 'Isoniazid', 'Peripheral Nervous System Diseases', 'Isoniazid commonly causes dose-related peripheral neuropathy, which results from the depletion of pyridoxine in the presence of the drug.  The neuropathy is usually preceded by paresthesias of the feet and hands.  Therapy with isoniazid should be administered cautiously in patients with preexisting peripheral neuropathy or risk factors for developing the condition, such as malnutrition, diabetes and alcoholism.  Pyridoxine (vitamin B6) at a dosage of 10 to 50 mg/day may prevent or attenuate isoniazid-related peripheral neuropathy and is recommended for these patients.', '3', '', 'DDInter', 0);
