INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58296, 6615, 'Isoniazid', 'Kidney Diseases', 'Isoniazid is metabolized primarily by acetylation and dehydrazination in the liver.  It is not significantly excreted by the kidney.  Dosage adjustments in renal impairment are generally not necessary except in slow acetylators with a creatinine clearance below 10 mL/min.  The rate of acetylation is genetically determined.  Approximately 50% of blacks and caucasians are slow acetylators, and the majority of Eskimos and Asians are rapid acetylators.', '2', '', 'DDInter', 0);
