INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58661, 2053, 'Isosorbide dinitrate', 'Anemia', 'Some manufacturers and medical references consider the use of nitrates and nitrites to be contraindicated in patients with severe anemia.  Nitrates and nitrites can cause methemoglobinemia, primarily in high-dose intravenous therapy or acute poisoning and in patients with NADH reductase deficiency.  However, elevations of methemoglobin may also occur with commonly used dosages.  While probably not of routine clinical significance, the increases may be important in certain patient populations such as those with coronary insufficiency or anemia.', '3', '', 'DDInter', 0);
