INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58677, 2053, 'Isosorbide dinitrate', 'Diseases requiring hemodialysis', 'The combined effect of organic nitrates or nitrites with hemodialysis, which often lowers blood pressure, can cause life-threatening hypotension.  Therapy with these agents should be administered cautiously in patients requiring hemodialysis.  The medication should be withheld before undergoing dialysis, and hemodynamic stability should be established prior to resumption of medication following dialysis.  Nitroglycerin and isosorbide dinitrate are moderately dialyzed.', '3', '', 'DDInter', 0);
