INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58701, 15837, 'Isosorbide dinitrate', 'Hypotension', 'Organic nitrates and nitrites may cause severe hypotension, syncope and shock, even with small doses.  Hypotension induced by these agents may be accompanied by paradoxical bradycardia and increased angina pectoris.  Therapy with nitrates and nitrites should be administered cautiously in patients who are volume-depleted or hypotensive (e.g., systolic blood pressure <90 mm Hg) or who, because of inadequate circulation to the brain or to other vital organs, would be unusually compromised by undue hypotension.  Patients should be in a sitting or recumbent position during and immediately after drug administration, and monitored for symptoms of severe hypotension such as nausea, vomiting, weakness, pallor, perspiration, and syncope.  Nitrate or nitrite therapy is considered contraindicated in patients with acute circulatory failure or shock.', '3', '', 'DDInter', 0);
