INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (201, 'Ribavirin', 'Lamivudine', 'Moderate', 'Concomitant use of oral ribavirin and nucleoside reverse transcriptase inhibitors (NRTIs) may increase the risk of symptomatic hyperlactatemia and lactic acidosis. Ribavirin itself is a nucleoside analogue, and these agents alone and in combination have been associated with the syndrome of lactic acidosis and severe hepatomegaly with steatosis. Other mitochondrial toxicities associated with the use of nucleoside analogues include lipodystrophy, peripheral neuropathy, and pancreatitis.', 'DDInter', 'Coadministration of ribavirin and NRTIs should be undertaken with caution and only if the benefit is anticipated to outweigh the potential risks. Patients should be advised to promptly seek medical attention if they experience symptoms of toxicity such as nausea, vomiting, abdominal pain/distention, fatigue, unexplained weight loss, tachypnea, dyspnea, motor weakness, numbness, tingling, and pain in hands and feet.', 'Synergism', 'Coadministration of ribavirin and NRTIs should be undertaken with caution and only if the benefit is anticipated to outweigh the potential risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/201/', '', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', 1767369485);
