INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (401, 'Zidovudine', 'Pegaspargase', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', 'DDInter', 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/401/', '', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, Ivosidenib, More', 'Paritaprevir, Glecaprevir, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (402, 'Zidovudine', 'Peginterferon alfa-2a', 'Moderate', 'Coadministration of zidovudine with other bone marrow depressive or cytotoxic agents may increase the risk and/or severity of zidovudine hematologic toxicity. Zidovudine has been associated with hematologic toxicities such as: neutropenia, granulocytopenia and severe anemia that required blood transfusions particularly in patients with advanced HIV disease.', 'DDInter', 'Hematological parameters should be closely monitored. Some authorities recommend dosages of one or both agents may be adjusted if necessary.', 'Synergism', 'Hematological parameters should be closely monitored.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/402/', '', 'Oprelvekin, Elapegademase, Sargramostim, Histamine, Pegademase, Pegfilgrastim, Filgrastim, Glatiramer', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (403, 'Zidovudine', 'Peginterferon alfa-2b', 'Major', 'The coadministration of interferons and zidovudine (AZT) may result in severe bone marrow toxicity, most often manifested as granulocytopenia and/or thrombocytopenia. The precise mechanism is unknown. These agents are individually myelotoxic, with potentially additive or synergistic effects; interferons may also decrease the metabolism of zidovudine.', 'DDInter', 'Caution is advised if concomitant therapy is necessary, particularly with the alfa interferons. Complete blood counts, differential and platelet counts should be performed regularly and drug dosages adjusted accordingly. Patients should be advised to promptly report symptoms such as chills, fever, sore throat, pale skin, or unusual fatigue to their physician. Cessation of one or all drugs may be necessary if toxicity develops.', 'Synergism', 'Caution is advised if concomitant therapy is necessary, particularly with the alfa interferons.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/403/', '', 'Oprelvekin, Elapegademase, Sargramostim, Histamine, Pegademase, Pegfilgrastim, Filgrastim, Glatiramer', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 1767369485);
