INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (477, 'Zidovudine', 'Trimethoprim', 'Minor', 'Limited data indicate that zidovudine may significantly increase the serum half-life of both trimethoprim (TMP) and sulfamethoxazole (SMX). The mechanism is not known, but may be related to an increase in the volume of distribution of each drug. In addition, trimethoprim can decrease the renal clearance of zidovudine and its glucuronide metabolite by inhibiting their tubular secretion. However, net clearance is not affected because zidovudine is mostly metabolized and the glucuronide may be eliminated via biliary excretion. This interaction is not likely to be clinically important except when hepatic glucuronidation is also impaired by liver disease or drug inhibition.', 'DDInter', 'Recommended management consists of monitoring for side effects of each drug and reducing dosages as necessary.', 'Synergism, Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/477/', '', 'Sulfamethizole, Sulfisoxazole, Rifamycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 1767369485);
