INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (957, 'Abciximab', 'Alteplase', 'Major', 'The intended and unintended effects of glycoprotein IIb/IIIa inhibitors and thrombolytic agents are additive, such that there is an increased frequency of major bleeding complications, including intracranial hemorrhage, retroperitoneal bleeding, spontaneous gastrointestinal (GI) and genitourinary (GU) bleeding among patients receiving both agents compared with the risk associated with the use of either agent alone.', 'DDInter', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. The successful use of combination therapy with glycoprotein IIb/IIIa inhibitors and reduced doses of thrombolytics has been reported in clinical trials. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Thrombolytic and/or glycoprotein inhibitor therapy should be discontinued immediately if serious or uncontrollable bleeding occurs.', 'Synergism', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/957/', '', 'Abciximab, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine, Nandrolone', 'Alteplase, Streptokinase, Antithrombin III human, Tenecteplase, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase', 1767369485);
