INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (996, 'Abciximab', 'Dicoumarol', 'Major', 'The risk of bleeding, including major bleeding (intracranial hemorrhage or a decrease in hemoglobin greater than 5 mg/dl) is significantly more likely when oral anticoagulants and glycoprotein IIb/IIIa inhibitors are given together.', 'DDInter', 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.2 times control. Bleeding precautions should be observed and patients should be closely monitored for clinical and laboratory evidence of adverse effects.', 'Synergism', 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/996/', '', 'Warfarin, Cilostazol, Dicoumarol, Antithrombin III human, Acenocoumarol, Selexipag, Anistreplase', 'Abciximab, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (997, 'Abciximab', 'Diflunisal', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.', 'DDInter', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/997/', '', 'Protein C, Acenocoumarol, Selexipag', 'Acetaminophen, Ziconotide, Methoxyflurane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (998, 'Abciximab', 'Dipyridamole', 'Major', 'The combination of glycoprotein IIb/IIIa (GP IIb/IIIa) inhibitors and other drugs that inhibit platelet aggregation may significantly increase the risk of bleeding, especially from arterial puncture sites. The mechanism is synergistic pharmacologic effects.', 'DDInter', 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/998/', '', 'Dipyridamole, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Ticlopidine', 'Abciximab, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (999, 'Drotrecogin alfa', 'Abciximab', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT). Treatment with drotrecogin alfa alone has been associated with serious and life-threatening bleeding episodes, including gastrointestinal, intracranial and retroperitoneal hemorrhage, although most severely ill septic patients are already at a high risk of bleeding because of coagulopathies associated with prolonged APTT and prothrombin time (PT).', 'DDInter', 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who have recently (within 7 days) received oral anticoagulants, platelet inhibitors, or aspirin (more than 650 mg/day). Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs. Continued use of other agents that may have contributed to the bleeding should be carefully assessed. Once adequate hemostasis is attained, drotrecogin alfa may be resumed if necessary.', 'Synergism', 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who have recently (within 7 days) received oral anticoagulants, platelet inhibitors, or aspirin (more than 650 mg/day).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/999/', '', 'Acenocoumarol, Selexipag, Drotrecogin alfa', 'Abciximab, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (1000, 'Duloxetine', 'Abciximab', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1000/', '', 'Protein C, Acenocoumarol, Selexipag', 'Maprotiline, St. John''s Wort, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, More', 1767369485);
