INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (1797, 'Brentuximab vedotin', 'Paclitaxel', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk. The peripheral neuropathy associated with brentuximab vedotin treatment is cumulative and primarily sensory, but cases of peripheral motor neuropathy have also been reported.', 'DDInter', 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy. Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness. Patients experiencing new, worsening, or recurrent neuropathy may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1797/', '', 'Irinotecan', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
