INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2101, 'Isotretinoin', 'Dexamethasone', 'Moderate', 'Theoretical concerns exist that isotretinoin and corticosteroids may have additive effects on bone loss. Decreased bone mineral density (osteomalacia, osteopenia, osteoporosis) and bone fractures have been reported with either treatment alone during prolonged administration. Avascular necrosis has also been reported with corticosteroids, particularly when given in high dosages or for prolonged periods. However, no formal clinical trials have been conducted to evaluate whether there is an interactive effect on bone loss during coadministration.', 'DDInter', 'Caution is advised when isotretinoin is used in combination with corticosteroids, particularly in patients with a history of osteomalacia, osteoporosis, or other disorders of bone metabolism. Bone-related laboratory and radiological tests should be considered.', 'Synergism', 'Caution is advised when isotretinoin is used in combination with corticosteroids, particularly in patients with a history of osteomalacia, osteoporosis, or other disorders of bone metabolism.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2101/', '', 'Minocycline, Vitamin A, Dexamethasone, Fluorometholone, Dapsone, Azelaic acid, Adapalene', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Ipratropium, Cromoglicic acid, More', 1767369485);
