INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2498, 'Fentanyl', 'Nefazodone', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fentanyl, which is primarily metabolized by the isoenzyme. Increased fentanyl concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Conversely, discontinuation of a CYP450 3A4 inhibitor could decrease fentanyl plasma concentrations, decrease opioid efficacy, and possibly even lead to a withdrawal syndrome in patients who had developed physical dependence to fentanyl.', 'DDInter', 'Patients receiving fentanyl with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed. This is particularly important when an inhibitor is added after a stable dose of fentanyl has been achieved. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur, such as dizziness, confusion, fainting, extreme sedation, unresponsiveness, bradycardia, slow or difficult breathing, and shortness of breath.', 'Metabolism', 'Patients receiving fentanyl with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2498/', '', 'Thiopental, Propofol, Naloxone', '', 1767369485);
