INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2538, 'Fentanyl', 'Pentazocine', 'Major', 'Concomitant use of opioids with other central nervous system (CNS) depressants including mixed agonist-antagonist or partial agonist opioids may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased.', 'DDInter', 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary (e.g., when initiating a switch from one opioid to the other), the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, and patients should be closely monitored for signs and symptoms of CNS and respiratory depression. Additional caution is advisable when a mixed agonist-antagonist or partial agonist opioid is added to an existing opioid regimen, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the mixed agonist-antagonist or partial agonist opioid.', 'Synergism', 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2538/', '', 'Fentanyl', 'Etomidate, Pentazocine, Acetylsalicylic acid, Acetaminophen, Ibuprofen', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2539, 'Fentanyl', 'Pentobarbital', 'Major', 'Barbiturates may potentiate the central nervous system (CNS) depressant effects of opioids. Concomitant use may result in profound sedation, respiratory depression, coma, and death. On the other hand, some barbiturates can also induce the hepatic metabolism of opioids that are metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, methadone and oxycodone, resulting in enhanced clearance. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on their opioid regimen following the addition of a barbiturate. Conversely, discontinuation of the barbiturate may increase plasma concentrations of the opioid and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate. Particular caution is advisable when a barbiturate is added to or withdrawn from therapy in patients receiving opioids that are CYP450 3A4 substrates, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the barbiturate and overdose following discontinuation. A dosage adjustment for one or both drugs may be required.', 'Synergism', 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2539/', '', 'Halothane, Isoflurane, Nitrous oxide, Desflurane, Enflurane, Sevoflurane, Etomidate, Naltrexone, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', '', 1767369485);
