INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2659, 'Fentanyl', 'Torasemide', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2659/', '', 'Etacrynic acid, Tinidazole, Ascorbic acid, Methazolamide, Oxytetracycline, Sulfamethoxazole, Trichlormethiazide, Sulfamethizole, Simeprevir, Tioconazole, Sulfadiazine, More', 'Halothane, Thiopental, Remifentanil, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Enflurane, Esketamine, Sevoflurane, Ketamine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2660, 'Fentanyl', 'Tramadol', 'Major', 'Concomitant use of tramadol with other central nervous system (CNS) depressants including other opioids may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension and seizures may also be increased. In patients who have been previously dependent on or chronically using opioids, tramadol can reinitiate physical dependence or precipitate withdrawal symptoms.', 'DDInter', 'Concomitant use of tramadol with other opioids should generally be avoided. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for development of seizures, respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Cough medications containing opioids (e.g., codeine, hydrocodone) should not be prescribed to patients on tramadol. In addition, tramadol should not be used in opioid-dependent patients, and use in patients who are chronically on opioids is also not recommended. Tramadol is contraindicated in patients with acute opioid intoxication.', 'Synergism', 'Concomitant use of tramadol with other opioids should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2660/', '', 'Fentanyl', 'Etomidate, Tramadol', 1767369485);
