INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (2762, 'Acalabrutinib', 'Cimetidine', 'Moderate', 'INTERVAL: Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of acalabrutinib and reduce its concentrations in plasma. The solubility of acalabrutinib is pH-dependent and decreases with increasing pH.', 'DDInter', 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided. If acid suppression therapy is required, H2-receptor antagonists or antacids should be considered. The manufacturer recommends taking acalabrutinib 2 hours before H2-receptor antagonists and separating the dosing of antacids by at least 2 hours.', 'Absorption', 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2762/', '', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Amoxicillin', 'Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erdafitinib, More', 1767369485);
