INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (3451, 'Procainamide', 'Lidocaine', 'Moderate', 'Limited data suggest that the combination of lidocaine and procainamide may result in an increased risk of conduction abnormalities, including complete AV heart block, and increased risk of nervous system side effects such as restlessness and hallucinations. Patients with preexisting conduction abnormalities are at higher risk for conduction abnormalities with the combination.', 'DDInter', 'Close monitoring of the patient''s hemodynamics, ECG, and mental status is recommended, especially in high-risk patients.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3451/', '', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Tripelennamine, Pheniramine, Bacitracin, Flurbiprofen, Ibuprofen, Chlorhexidine, More', 'Moricizine, Lidocaine, Flecainide', 1767369485);
