INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (3690, 'Hyoscyamine', 'Phenylephrine', 'Moderate', 'The pressor response to phenylephrine may be potentiated by the vagolytic effect of atropine, which inhibits the reflex bradycardia that would normally accompany any increases in blood pressure induced by phenylephrine. Other antimuscarinic agents may also participate in this interaction, although clinical data are lacking.', 'DDInter', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents. Cardiovascular status, including blood pressure and heart rate, should be closely monitored. When using ophthalmic formulations, measures to minimize systemic absorption should be employed, such as digital compression of the lacrimal sac or lid closure after instillation. A milder strength (< 10%) is preferable if phenylephrine ophthalmic solution is given.', 'Synergism', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3690/', '', 'Tropicamide, Ephedrine, Ketorolac, Flunisolide, Betamethasone, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Cromoglicic acid, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (3691, 'Ifosfamide', 'Chlorpheniramine', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', 'DDInter', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3691/', '', 'Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', 'Temozolomide, Thiotepa, Carmustine, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, Melphalan, More', 1767369485);
