INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (3694, 'Imipramine', 'Phenylephrine', 'Major', 'Tricyclic antidepressants (TCAs) may markedly enhance the pressor response to parenteral direct-acting sympathomimetic agents and vasoconstrictor-containing local anesthetics.', 'DDInter', 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.g., treatment of anaphylaxis). If concomitant use is necessary, initial dose and rate of administration of the sympathomimetic should be reduced, and cardiovascular status including blood pressure should be monitored closely. Although clinical data are lacking, it may be prudent to follow the same precaution with mixed-acting sympathomimetic agents.', 'Synergism', 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3694/', '', 'Tropicamide, Ketorolac, Mupirocin, Ciclesonide, Mometasone furoate, Mometasone, Flunisolide, Olopatadine, Hydrocortisone, Vitamin A, Cromoglicic acid, More', 'Citalopram, Sertraline, Paroxetine, Maprotiline, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Vortioxetine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (3695, 'Indacaterol', 'Phenylephrine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3695/', '', 'Ketorolac, Homatropine, Atropine, Scopolamine, Methscopolamine, Tropicamide, Mupirocin, Mometasone furoate, Vitamin A, Olopatadine, Cromoglicic acid, More', 'Mometasone furoate, Umeclidinium, Budesonide, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 1767369485);
