INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4293, 'Pilocarpine', 'Acebutolol', 'Moderate', 'The concomitant use of oral pilocarpine and beta blockers may lead to cardiac conduction disturbances. The mechanism is unknown.', 'DDInter', 'If these drugs must be given together, consider the possibility for arrhythmias and administer with caution. Patients should be advised to notify their physician if they experience irregular or slow heartbeats, palpitations, or syncope.', 'Others', 'If these drugs must be given together, consider the possibility for arrhythmias and administer with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4297/', '', 'Ambenonium, Bethanechol, Pyridostigmine, Methazolamide, Acetylcholine, Betaxolol, Diclofenamide, Bimatoprost, Travoprost, Carteolol, Brinzolamide, More', '', 1767369485);
