INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4596, 'Dihydrocodeine', 'Boceprevir', 'Moderate', 'Coadministration with boceprevir may increase the plasma concentrations of drugs that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by boceprevir.', 'DDInter', 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever boceprevir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4600/', '', 'Codeine, Morphine, Naltrexone, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, Acetaminophen, Dezocine, Opium, Dextropropoxyphene, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4597, 'Dihydrocodeine', 'Bosentan', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', 'DDInter', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4601/', '', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, More', 'Tapentadol, Oxymorphone, Meperidine, Hydromorphone, Dezocine, Opium, Dextropropoxyphene, Pentazocine, Naloxone, Nalbuphine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4598, 'Dihydrocodeine', 'Brexanolone', 'Moderate', 'Coadministration with central nervous system (CNS) depressants (e.g., alcohol, benzodiazepines, opioids) or antidepressants may enhance the sedative effects of brexanolone and increase the likelihood or severity of sedation-related adverse reactions. Patients treated with brexanolone are at risk of excessive sedation or sudden loss of consciousness during administration.', 'DDInter', 'Caution is advised during concomitant use of brexanolone with CNS depressants, antidepressants, or other agents that cause sedation. Patients should be closely monitored for excessive sedation and sudden loss of consciousness and have continuous pulse oximetry monitoring. During the brexanolone infusion, monitor for sedative effects every 2 hours during planned, non-sleep periods, and immediately stop the infusion if there are signs or symptoms of excessive sedation. After symptoms resolve, the infusion may be resumed at the same or lower dose as clinically appropriate. If pulse oximetry reveals hypoxia, immediately stop the infusion and do not resume infusion following resolution of the hypoxia. Patients should be cautioned against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until sedative effects of brexanolone and other concomitant medications have dissipated.', 'Synergism', 'Caution is advised during concomitant use of brexanolone with CNS depressants, antidepressants, or other agents that cause sedation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4602/', '', 'Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4599, 'Dihydrocodeine', 'Brexpiprazole', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4603/', '', 'Naltrexone, Acetaminophen, Ibuprofen, Acetylsalicylic acid, Naloxone', 'Amisulpride', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4600, 'Dihydrocodeine', 'Brigatinib', 'Moderate', 'Coadministration with brigatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Brigatinib has been shown in vitro to induce CYP450 3A4 at clinically relevant plasma concentrations.', 'DDInter', 'Caution is advised when brigatinib is used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever brigatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when brigatinib is used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4604/', '', 'Codeine, Naltrexone, Oxymorphone, Naloxone, Tapentadol, Meperidine, Acetaminophen, Hydromorphone, Dezocine, Opium, Dextropropoxyphene, More', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, More', 1767369485);
