INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4722, 'Fluvoxamine', 'Caffeine', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of caffeine in a dose-dependent manner. The proposed mechanism is fluvoxamine inhibition of CYP450 1A2, the isoenzyme responsible for the metabolic clearance of caffeine.', 'DDInter', 'Caution is advised if caffeine is administered concurrently with fluvoxamine. Dosage adjustment should be considered in patients who experience excessive adverse effects of caffeine such as nausea, vomiting, headache, nervousness, irritability, agitation, restlessness, insomnia, tinnitus, tremors, and tachycardia. Alternatively, use of a selective serotonin reuptake inhibitor that does not significantly affect CYP450 1A2 activity (e.g., citalopram, escitalopram, fluoxetine, paroxetine, sertraline) may be considered.', 'Metabolism', 'Caution is advised if caffeine is administered concurrently with fluvoxamine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4726/', '', 'Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Isocarboxazid, Amoxapine, Imipramine, More', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Ivermectin, Cromoglicic acid, Dupilumab, Tralokinumab, More', 1767369485);
