INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (5396, 'Codeine', 'Tofacitinib', 'Moderate', 'Baricitinib and tofacitinib, both Janus kinase (JAK) inhibitors, have been associated with an increased risk of diverticulitis (DV) and gastrointestinal (GI) perforation, particularly in patients with risk factors (e.g., history of diverticulosis or diverticulitis, concomitant use of other agents associated with DV).', 'DDInter', 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids. Patients should be advised to contact their healthcare provider if they experience signs of DV or GI perforation, such as severe abdominal pain, fever, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5400/', '', 'Acetaminophen, Naloxone, Naltrexone, Benzonatate, Clofedanol, Dextromethorphan, Pentoxyverine', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 1767369485);
