INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (5804, 'Dezocine', 'Tramadol', 'Major', 'Concomitant use of tramadol increases the seizure risk in patients taking other opioids. These agents are often individually epileptogenic and may have additive effects on seizure threshold during coadministration. CNS- and respiratory-depressant effects may also be additive. In patients who have been previously dependent on or chronically using opioids, tramadol can also reinitiate physical dependence or precipitate withdrawal symptoms. Mixed opioid agonist-antagonist analgesics such as buprenorphine, butorphanol, nalbuphine, and pentazocine may theoretically decrease the analgesic effects of tramadol or cause withdrawal symptoms in patients who have been taking tramadol.', 'DDInter', 'Concomitant use of tramadol and other opioids, including mixed agonist/antagonist analgesics, should be avoided in general. Tramadol should not be used in opioid-dependent patients, and use in patients who are chronically on opioids is also not recommended. Tramadol is contraindicated in patients with acute opioid intoxication. Tramadol dosage should be reduced if it must be used in patients receiving opioids. Patients should be monitored for development of seizures and CNS and respiratory depression.', 'Synergism', 'Concomitant use of tramadol and other opioids, including mixed agonist/antagonist analgesics, should be avoided in general.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5808/', '', 'Acetaminophen, Dezocine, Ibuprofen, Celecoxib, Acetylsalicylic acid', 'Tramadol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (5805, 'Diazepam', 'Tramadol', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5809/', '', '', '', 1767369485);
