INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6199, 'Acetazolamide', 'Arformoterol', 'Moderate', 'Coadministration with beta-2 adrenergic agonists may potentiate the hypokalemic effects of potassium-wasting diuretics. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations. Since QT prolongation is a possible side effect of beta-2 agonists, exacerbation of hypokalemia may increase the risk of torsade de pointes and other serious arrhythmias. The interaction may be more likely with systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or concomitant theophylline or corticosteroid therapy.', 'DDInter', 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics. Serum potassium level and cardiovascular status should be monitored, especially if the beta-2 agonist is administered systemically or by nebulizer. Patients should be advised to notify their physician if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6203/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6200, 'Acetazolamide', 'Arsenic trioxide', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of arsenic trioxide with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6204/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Darunavir, Probenecid, Tioconazole, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
