INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6557, 'Famotidine', 'Cyanocobalamin', 'Minor', 'By reducing or suppressing gastric acid secretion, H2-receptor antagonists and proton pump inhibitors may interfere with the gastrointestinal absorption of vitamin B12, a process that is dependent on the presence of gastric acid and pepsin. Clinical studies have shown that dietary (i.e., protein-bound) vitamin B12 malabsorption can occur during treatment with these agents, particularly proton pump inhibitors, although the likelihood of developing clinically significant deficiency over time is unknown.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6561/', '', 'Hydroxocobalamin, Iron protein succinylate, Iron, Ferrous gluconate, Ferric maltol, Iron sucrose, Ferrous fumarate', 'Tinidazole, Amoxicillin, Tetracycline, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Levofloxacin', 1767369485);
