INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6581, 'Famotidine', 'Enoxacin', 'Minor', 'Depending on the route of administration, enoxacin effects may be altered by certain histamine-2 (H2) receptor antagonists. Oral enoxacin effects may be decreased due to reduced gastrointestinal solubility and absorption as a result of H2 antagonist-induced reductions in gastric pH. Reports of this interaction exist for parenteral ranitidine. On the other hand, pharmacologic effects of parenteral enoxacin may be increased by cimetidine. The mechanism is speculated to be competition for active renal secretion. Reports of enoxacin interactivity with other H2 antagonists do not exist, although theoretically, all H2 antagonists may produce a similar effect on oral enoxacin. Close clinical monitoring for altered enoxacin effect is recommended in patients receiving concomitant enoxacin and an H2 antagonist.', 'DDInter', '-', 'Absorption, Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6585/', '', 'Trovafloxacin, Delafloxacin, Nalidixic acid, Cinoxacin', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Esomeprazole, More', 1767369485);
