INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6603, 'Fosphenytoin', 'Famotidine', 'Moderate', 'Coadministration with famotidine or ranitidine may rarely increase the plasma concentrations of phenytoin, resulting in toxicity. The mechanism of interaction is unknown.', 'DDInter', 'Until more information is available, caution is advised if phenytoin is prescribed in combination with famotidine or ranitidine, particularly in elderly patients. Clinicians should be alert for signs and symptoms of phenytoin toxicity such as ataxia, incoordination, tremor, nystagmus, hypotension, slurred speech, lethargy, nausea, vomiting, mental confusion, and psychosis. The possibility of an interaction should be considered if toxicity occurs shortly (e.g., within a month) after initiation or change of dosage of the H2-receptor antagonist. Both phenytoin and the H2-receptor antagonist may need to be withdrawn until the patient recovers.', 'Others', 'Until more information is available, caution is advised if phenytoin is prescribed in combination with famotidine or ranitidine, particularly in elderly patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6607/', '', 'Tetracycline, Levofloxacin, Rabeprazole, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Amoxicillin', 'Brivaracetam, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 1767369485);
