INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6693, 'Famotidine', 'Pazopanib', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of pazopanib and reduce its concentrations in plasma. The solubility of pazopanib is pH-dependent, thus an increase in pH may interfere with its absorption.', 'DDInter', 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. If acid-suppression therapy is required, short-acting antacids should be considered, with dosing separated by several hours from pazopanib dosing. Similarly, when coadministration with an H2-receptor antagonist is required, it has been recommended that pazopanib be taken without food at least 2 hours before or 10 hours after a dose of the H2-receptor antagonist.', 'Absorption', 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6697/', '', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Vonoprazan, Amoxicillin', 'Tepotinib, Larotrectinib, Avapritinib, Fedratinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Duvelisib, Tivozanib, More', 1767369485);
